mapper.select:


    d.project_id=#{projectId}
     and d.project_num = #{defect.defectNum}
     and d.defect_type = #{defect.defectType, typeHandler=com.cat2bug.api.domain.handle.ApiDefectTypeEnumTypeHandler }
     and d.defect_name like concat('%', #{defect.defectName}, '%')
     and d.module_version like concat('%', #{defect.version}, '%')


     and d.defect_state = #{defect.defectState, typeHandler=com.cat2bug.api.domain.handle.ApiDefectStateEnumTypeHandler}





     and

            FIND_IN_SET(#{b},un.user_names)




     and d.defect_level = #{defect.defectLevel}

order by d.project_num DESC, d.update_time DESC

select defect_id from sys_defect
where
    project_id = #{projectId}

    and defect_key = #{defectKey}


    and defect_group_key = #{defectGroupKey}

mapper.update:
update sys_defect
defect_type = #{defectType, typeHandler=com.cat2bug.api.domain.handle.ApiDefectTypeEnumTypeHandler}, defect_name = #{defectName}, defect_describe = #{defectDescribe}, annex_urls = #{annexUrlList, typeHandler=com.cat2bug.api.domain.handle.CommaArrayTypeHandler}, img_urls = #{imgUrlList, typeHandler=com.cat2bug.api.domain.handle.CommaArrayTypeHandler}, project_id = #{projectId}, module_id = #{deliverableId}, module_version = #{version}, update_time = #{updateTime}, update_by = #{updateBy}, defect_state = #{defectState, typeHandler=com.cat2bug.api.domain.handle.ApiDefectStateEnumTypeHandler}, handle_by = #{handleBy, typeHandler=com.cat2bug.common.core.domain.handle.LongArrayTypeHandler}, defect_level = #{defectLevel},
where defect_id = #{defectId}
