mapper.select:


     and c.case_name like concat('%', #{caseName}, '%')
     and c.module_id = #{moduleId}
     and c.case_type = #{caseType}
     and c.case_expect = #{caseExpect}
     and c.case_level = #{caseLevel}
     and c.case_preconditions = #{casePreconditions}
     and c.case_num = #{caseNum}
     and c.project_id = #{projectId}

     and c.module_id in

            #{moduleId}



ORDER BY c.update_time DESC

mapper.update:
update sys_case
case_name = #{caseName}, module_id = #{moduleId}, case_type = #{caseType}, case_expect = #{caseExpect}, case_step = #{caseStep,typeHandler=com.cat2bug.system.domain.handle.SysCaseStepArrayTypeHandler}, case_level = #{caseLevel}, case_preconditions = #{casePreconditions}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, case_num = #{caseNum}, project_id = #{projectId}, remark = #{remark}, img_urls = #{imgUrls},
where case_id = #{caseId}
