mapper.sql:
SELECT DISTINCT r.role_id, r.role_name, r.role_name_i18n_key, r.role_key, r.role_sort, r.data_scope, r.menu_check_strictly, r.dept_check_strictly, r.STATUS, r.del_flag, r.create_time, r.remark, r.is_team_role, r.is_project_role FROM sys_role r LEFT JOIN ( SELECT 'system' AS src, '' AS src_id, user_id, role_id FROM sys_user_role
UNION SELECT 'team' as src, ut.team_id as src_id, ut.user_id, utr.role_id FROM sys_user_team_role utr LEFT JOIN sys_user_team ut ON ut.user_team_id=utr.user_team_id AND ut.team_id = #{ teamId }
UNION SELECT 'project' as src, up.project_id as src_id, up.user_id, upr.role_id FROM sys_user_project_role upr LEFT JOIN sys_user_project up ON up.user_project_id=upr.user_project_id AND up.project_id = #{ projectId }
) ur ON ur.role_id = r.role_id LEFT JOIN sys_user u ON u.user_id = ur.user_id LEFT JOIN sys_dept d ON u.dept_id = d.dept_id

mapper.select:

where r.del_flag = '0'

	AND r.role_id = #{roleId}


	AND r.role_name like concat('%', #{roleName}, '%')


	AND r.status = #{status}


	AND r.role_key like concat('%', #{roleKey}, '%')


	and date_format(r.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


	and date_format(r.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


	AND r.is_team_role = #{isTeamRole}


	AND r.is_project_role = #{isProjectRole}


${params.dataScope}
order by r.role_sort

-- 		select r.role_id
--         from sys_role r
-- 	        left join sys_user_role ur on ur.role_id = r.role_id
-- 	        left join sys_user u on u.user_id = ur.user_id
-- 	    where u.user_id = #{userId}
		SELECT r.role_id FROM
		(SELECT 'system' as src, '' as src_id, user_id, role_id FROM sys_user_role

			UNION
			SELECT 'team' as src, ut.team_id as src_id, ut.user_id, utr.role_id FROM sys_user_team_role utr LEFT JOIN sys_user_team ut ON ut.user_team_id=utr.user_team_id AND ut.team_id = #{ teamId }


			UNION
			SELECT 'project' as src, up.project_id as src_id, up.user_id, upr.role_id FROM sys_user_project_role upr LEFT JOIN sys_user_project up ON up.user_project_id=upr.user_project_id AND up.project_id = #{ projectId }
		) r
		where r.user_id = #{userId}

mapper.update:
update sys_role
role_name = #{roleName}, role_name_i18n_key = #{roleNameI18nKey}, role_key = #{roleKey}, role_sort = #{roleSort}, data_scope = #{dataScope}, menu_check_strictly = #{menuCheckStrictly}, dept_check_strictly = #{deptCheckStrictly}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, is_team_role = #{isTeamRole}, is_project_role = #{isProjectRole}, update_time = sysdate()
where role_id = #{roleId}
